<?php

    require("conexion.php");

    session_start();

    if(!isset($_SESSION['iniciado'])){

        header("location:InicioSesion.php");
        
    }

    $conn=new conexion("kuitmine");
    $sql="SELECT * FROM `usuario` INNER JOIN `detallesusuario`
        ON `usuario`.`Id_detallesUsuario` = `detallesusuario`.`Id_detallesUsuario`
        WHERE `usuario`.`Id_usuario` =".$_SESSION['iniciado'];
    $resultUser=$conn->consultar($sql);

    //Definimos las variables que resiviran los datos del producto.
    $Nombre;
    $Descripcion;
    $Precio;
    $Stock;
    $Categoria;
    $ImagenPrin;
    $ImagenesSecu;
    $ImagenesSecuCount;
    $arrayImg = [];
    $i=0;
    $user=0;

    //Creamos una variable DateTime para obtener la fecha y hora;
    $fecha=new DateTime();


    if(isset($_REQUEST["btnSubir"])){
        $Nombre = $_POST['Nombre']; 
        $Descripcion = $_POST['Descripcion'];
        $Precio = $_POST['Precio'];
        $Stock = $_POST['Stock'];
        $Categoria = $_POST['Categoria'];
        $IVA = $_POST["IVA"];
        $Descu = $_POST["Descu"];

        $ImagenPrin = $fecha->getTimestamp()."_".$_FILES['ImgPrin']['name'];
        $ImagenPrinTemp=$_FILES['ImgPrin']['tmp_name'];
        move_uploaded_file($ImagenPrinTemp, "imgSubidas/".$ImagenPrin);

        $ImagenesSecu = $_FILES['ImgSecun']['name'];
        $imagenTemporal= $_FILES['ImgSecun']['tmp_name'];
        $ImagenesSecuCount = count($_FILES['ImgSecun']['name']);

        for($i;$i<$ImagenesSecuCount; $i++){
            $srand2 = rand(1,5);
            $arrayImg[$i] = $fecha->getTimestamp()."_"."secun".$ImagenesSecu[$i];
            $imagenTemporal2= $imagenTemporal[$i];
            move_uploaded_file($imagenTemporal2, "imgSubidas/".$arrayImg[$i]);
        }

        date_default_timezone_set('America/Bogota');

        $fechaActual = date("d-m-Y");
        $horaActual = date("h:i:s");

        $fh = array($fechaActual, $horaActual);
        $fh_s = implode(",",$fh);

        $arrayImgEnco= implode(",",$arrayImg);

        $userId = $resultUser[0]["Id_usuario"]; 

        $sql = "INSERT INTO `productoDetail` (`fh_creacion`, `imagenesSecun`) VALUES ('$fh_s', '$arrayImgEnco')";
        $result4 = $conn->ejecutar($sql);

        $sql2 = "INSERT INTO `producto` (`Nombre`, `Descripcion`, `Precio`, `Stock`, `imagenPrin`, `Categoria`, `IVA`, `Descuento`, `Id_productoDetail`, `Id_usuario`)
                VALUES ('$Nombre', '$Descripcion', $Precio, $Stock, '$ImagenPrin', '$Categoria', '$IVA', '$Descu', $result4, $userId)";
        $result3 = $conn->ejecutar($sql2);

        $conn->cerrar();
        header("location:Administracion.php");
    }

    
?>


